function [] = off_to_voxel(file_path, res=32, num_rotate=12)  pkg load image  result=logical(zeros(1,num_rotate,res,res,res));  [vertices,faces] = read_off(file_path);  FV.faces = faces';  for j = 1:num_rotate      theta = 2*pi*(j-1)/num_rotate;      rot_mat=[cos(theta) sin(theta) 0;-sin(theta) cos(theta) 0; 0 0 1];      FV.vertices = (rot_mat*vertices)';         result(1,j,:,:,:)=logical(polygon2voxel(FV,[res, res, res],'auto'));  end  save('-V7', '../mat_data/voxel.mat', 'result');end