function [mat_file] = off2voxel(off_file, res=32, num_rotate=12)  pkg load image  result=logical(zeros(1,num_rotate,res,res,res));  [vertices,faces] = read_off(off_file);  FV.faces = faces';  for i = 1:num_rotate      theta = 2*pi*(i-1)/num_rotate;      rot_mat=[cos(theta) sin(theta) 0;-sin(theta) cos(theta) 0; 0 0 1];      FV.vertices = (rot_mat*vertices)';         result(1,i,:,:,:)=logical(polygon2voxel(FV,[res, res, res],'auto'));  end  mat_file = strrep(off_file,'.off','.mat');  save('-V7', mat_file, 'result');end